import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [showNavbar, setShowNavbar] = useState(true);
  const [prevScrollPos, setPrevScrollPos] = useState(window.scrollY);
  const [menuOpen, setMenuOpen] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const navRef = useRef();
  const [coursesOpen, setCoursesOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.scrollY;
      setShowNavbar(prevScrollPos > currentScrollPos || currentScrollPos < 10);
      setPrevScrollPos(currentScrollPos);
    };
    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [prevScrollPos]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (navRef.current && !navRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const isActive = (path) => location.pathname === path;

  const handleLinkClick = (path) => {
    navigate(path);
    window.scrollTo(0, 0);
    setMenuOpen(false);
  };

  return (
    <>
      <nav
        className={`navbar ${showNavbar ? 'visible' : 'hidden'}`}
        ref={navRef}
        aria-label="Primary Navigation"
      >
        <div className="navbar-container">
          <div className="logo-container" onClick={() => handleLinkClick('/')}>
            <img src="/Images/logo.svg" alt="Logo" className="logo" />
            <span className="brand-name">YourTrust</span>
          </div>

          <div className={`nav-links ${menuOpen ? 'open' : ''}`} role="menubar" aria-hidden={!menuOpen}>
            <Link
              to="/"
              role="menuitem"
              className={`nav-link ${isActive('/') ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                handleLinkClick('/');
              }}
            >
              <span>Home</span>
              <i />
            </Link>
            <Link
              to="/about"
              role="menuitem"
              className={`nav-link ${isActive('/about') ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                handleLinkClick('/about');
              }}
            >
              <span>About</span>
              <i />
            </Link>
            <Link
              to="/donation"
              role="menuitem"
              className={`nav-link ${isActive('/donation') ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                handleLinkClick('/donation');
              }}
            >
              <span>Donation</span>
              <i />
            </Link>
            <Link
              to="/event"
              role="menuitem"
              className={`nav-link ${isActive('/event') ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                handleLinkClick('/event');
              }}
            >
              <span>Event</span>
              <i />
            </Link>
            <Link
              to="/blog"
              role="menuitem"
              className={`nav-link ${isActive('/blog') ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                handleLinkClick('/blog');
              }}
            >
              <span>Blog</span>
              <i />
            </Link>
            <Link
              to="/contact"
              role="menuitem"
              className={`nav-link ${isActive('/contact') ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                handleLinkClick('/contact');
              }}
            >
              <span>Contact</span>
              <i />
            </Link>
          </div>

          <div className="right-section">
            <div className="right-inner">
              <button
                className="join-button"
                onClick={() => handleLinkClick('/contact')}
                aria-label="Join With Us"
              >
                <span className="btn-glow" />
                <span className="btn-inner">
                  <svg viewBox="0 0 24 24" width="18" height="18" aria-hidden="true">
                    <path d="M12 12c2.761 0 5-2.462 5-5.5S14.761 1 12 1 7 3.462 7 6.5 9.239 12 12 12zm0 2c-4.418 0-8 2.91-8 6.5 0 .828.672 1.5 1.5 1.5h13c.828 0 1.5-.672 1.5-1.5 0-3.59-3.582-6.5-8-6.5z" />
                  </svg>
                  Join With Us
                </span>
              </button>

              <button
                className={`menu-icon ${menuOpen ? 'active' : ''}`}
                onClick={() => setMenuOpen(!menuOpen)}
                aria-label="Toggle Menu"
                aria-expanded={menuOpen}
                aria-controls="mobile-menu"
              >
                <div className="hamburger">
                  <span />
                  <span />
                  <span />
                </div>
              </button>
            </div>
          </div>
        </div>
        <div
          id="mobile-menu"
          className={`mobile-backdrop ${menuOpen ? 'show' : ''}`}
          onClick={() => setMenuOpen(false)}
          aria-hidden={!menuOpen}
        />
      </nav>
      <div className="nav-spacer" />
    </>
  );
}

export default Navbar;
