import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [showNavbar, setShowNavbar] = useState(true);
  const [prevScrollPos, setPrevScrollPos] = useState(window.scrollY);
  const [menuOpen, setMenuOpen] = useState(false);
  const [programsOpen, setProgramsOpen] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const navRef = useRef();

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.scrollY;
      setShowNavbar(prevScrollPos > currentScrollPos || currentScrollPos < 10);
      setPrevScrollPos(currentScrollPos);
    };
    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [prevScrollPos]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (navRef.current && !navRef.current.contains(event.target)) {
        setMenuOpen(false);
        setProgramsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const isActive = (path) => location.pathname === path;

  const handleLinkClick = (path) => {
    navigate(path);
    window.scrollTo(0, 0);
    setMenuOpen(false);
    setProgramsOpen(false);
  };

  const programs = [
    { label: 'Education Support', path: '/programs/education' },
    { label: 'Medical & Health Camps', path: '/programs/health' },
    { label: 'Women Empowerment', path: '/programs/women-empowerment' },
    { label: 'Legal Aid & Awareness', path: '/programs/legal-aid' },
    { label: 'Environment & Irrigation', path: '/programs/environment' },
    { label: 'Agriculture & Rural Dev', path: '/programs/agriculture' },
    { label: 'Youth & Skills (SIGs)', path: '/programs/youth-skills' },
    { label: 'Human Rights & Equality', path: '/programs/human-rights' }
  ];

  return (
    <nav className={`navbar ${showNavbar ? 'visible' : 'hidden'}`} ref={navRef}>
      <div className="navbar-backdrop" />
      <div className="navbar-container">
        <div className="logo-container" onClick={() => handleLinkClick('/')}>
          <img src="/Images/logo.svg" alt="Logo" className="logo" />
          <span className="brand">Charity Trust</span>
        </div>

        <div className={`nav-links ${menuOpen ? 'open' : ''}`}>
          <Link to="/" className={isActive('/') ? 'active' : ''} onClick={(e) => { e.preventDefault(); handleLinkClick('/'); }}>
            <span>Home</span>
          </Link>
          <Link to="/about" className={isActive('/about') ? 'active' : ''} onClick={(e) => { e.preventDefault(); handleLinkClick('/about'); }}>
            <span>About</span>
          </Link>

          <div className={`dropdown ${programsOpen ? 'open' : ''}`}>
            <button
              className={`drop-trigger ${location.pathname.startsWith('/programs') ? 'active' : ''}`}
              onClick={() => setProgramsOpen(!programsOpen)}
              aria-expanded={programsOpen}
              aria-haspopup="true"
            >
              <span>Programs</span>
              <span className="chev" />
            </button>
            <div className="drop-menu" role="menu">
              {programs.map((item) => (
                <button key={item.path} className="drop-item" onClick={() => handleLinkClick(item.path)} role="menuitem">
                  {item.label}
                </button>
              ))}
            </div>
          </div>

          <Link to="/gallery" className={isActive('/gallery') ? 'active' : ''} onClick={(e) => { e.preventDefault(); handleLinkClick('/gallery'); }}>
            <span>Gallery</span>
          </Link>
          <Link to="/contact" className={isActive('/contact') ? 'active' : ''} onClick={(e) => { e.preventDefault(); handleLinkClick('/contact'); }}>
            <span>Contact</span>
          </Link>
          <Link to="/donation" className={`donation-link ${isActive('/donation') ? 'active' : ''}`} onClick={(e) => { e.preventDefault(); handleLinkClick('/donation'); }}>
            <span>Donate</span>
          </Link>

          <div className="mobile-cta">
            <button className="join-button" onClick={() => handleLinkClick('/volunteer')}>Volunteer</button>
          </div>
        </div>

        <div className="right-section">
          <div className="right-inner">
            <button className="ghost-button desktop-only" onClick={() => handleLinkClick('/volunteer')}>Volunteer</button>
            <button className="join-button desktop-only" onClick={() => handleLinkClick('/donation')}>Donate</button>
            <button
              className={`menu-icon ${menuOpen ? 'active' : ''}`}
              onClick={() => setMenuOpen(!menuOpen)}
              aria-expanded={menuOpen}
              aria-label="Toggle menu"
            >
              <span className="menu-bar top" />
              <span className="menu-bar middle" />
              <span className="menu-bar bottom" />
            </button>
          </div>
        </div>
      </div>
      <div className={`active-indicator ${location.pathname.replace('/', '') || 'home'}`} />
    </nav>
  );
}

export default Navbar;
