          <div className="home-section9-head">
            <h2 className="home-section9-title">our organization provides</h2>
            <p className="home-section9-desc">
              Our organization provides everything possible to provide children around the world with everything they need.
            </p>
          </div>

          <div className="home-section9-grid">
            {items.map((it, i) => (
              <div className="s9-item" key={i} style={{ animationDelay: `${0.06 * i}s` }}>
                <div className="s9-icon-wrap">
                  <img src={it.img} alt={it.title} className="s9-icon" />
                </div>
                <div className="s9-copy">
                  <h3 className="s9-item-title">{it.title}</h3>
                  <p className="s9-item-desc">{it.desc}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>







      <section className={`home-section7-wrapper ${visible ? 'show' : ''}`} ref={ref}>
        <div className="home-section7-container">
          <div className="home-section7-head">
            <span className="home-section7-subtitle"><FaHeart className="icon" /> Stories, Updates, Answers</span>
            <h2 className="home-section7-title">Whatâ€™s New, Who We Help, How You Can Join</h2>
            <p className="home-section7-desc">Explore our latest milestones, hear from our elders, and get quick answers to common questions.</p>
          </div>

          <div className="home-section7-grid">
            <div className="home-section7-timeline">
              {milestones.map((m, i) => (
                <div key={i} className="s7-tl-item">
                  <div className="s7-tl-dot">
                    <div className="s7-tl-dot-inner">{m.icon}</div>
                  </div>
                  <div className="s7-tl-card">
                    <div className="s7-tl-date">{m.date}</div>
                    <div className="s7-tl-title">{m.title}</div>
                    <div className="s7-tl-desc">{m.desc}</div>
                    <a href="/event" className="s7-tl-link">Learn More <FaArrowRight /></a>
                  </div>
                </div>
              ))}
            </div>

            <div className="home-section7-spotlight">
              <div className="s7-spotlight-card">
                <div className="s7-spotlight-media">
                  <img src={testimonials[activeTestimonial].image} alt={testimonials[activeTestimonial].name} />
                  <div className="s7-spotlight-badge">Elder Voices</div>
                </div>
                <div className="s7-spotlight-quote">
                  <FaQuoteLeft className="s7-quote-icon" />
                  <p>{testimonials[activeTestimonial].quote}</p>
                  <span className="s7-quote-name">{testimonials[activeTestimonial].name}</span>
                </div>
                <div className="s7-spotlight-dots">
                  {testimonials.map((_, idx) => (
                    <button
                      key={idx}
                      className={`s7-dot ${idx === activeTestimonial ? 'active' : ''}`}
                      onClick={() => setActiveTestimonial(idx)}
                      aria-label={`Show testimonial ${idx + 1}`}
                    />
                  ))}
                </div>
              </div>

              <div className="s7-info-pills">
                <a href="/donation" className="s7-pill">
                  <span>â‚¹500</span>
                  <em>1 Day Meals</em>
                </a>
                <a href="/donation" className="s7-pill">
                  <span>â‚¹3,500</span>
                  <em>Week of Care</em>
                </a>
                <a href="/donation" className="s7-pill">
                  <span>â‚¹15,000</span>
                  <em>Medical Support</em>
                </a>
                <a href="/donation" className="s7-pill">
                  <span>â‚¹30,000</span>
                  <em>Bed & Essentials</em>
                </a>
              </div>
            </div>

            <div className="home-section7-faq">
              <div className="s7-faq-list">
                {faqs.map((f, i) => (
                  <div key={i} className={`s7-faq-item ${openFaq === i ? 'open' : ''}`}>
                    <button className="s7-faq-q" onClick={() => setOpenFaq(openFaq === i ? null : i)}>
                      <FaQuestionCircle className="q-icon" />
                      <span>{f.q}</span>
                      <FaChevronDown className="chev" />
                    </button>
                    <div className="s7-faq-a">
                      <p>{f.a}</p>
                    </div>
                  </div>
                ))}
              </div>
              <div className="s7-cta">
                <a href="/contact" className="s7-cta-btn">
                  Become A Volunteer
                </a>
                <a href="/donation" className="s7-cta-link">Prefer to donate? Start here</a>
              </div>
            </div>
          </div>
        </div>
      </section>


      <section className={`home-section8-wrapper ${show ? 'show' : ''}`} ref={ref}>
        <div className="home-section8-container">
          <div className="home-section8-head">
            <span className="home-section8-subtitle"><FaHeart className="icon" /> Programs that change lives</span>
            <h2 className="home-section8-title">Choose A Cause To Power Today</h2>
            <p className="home-section8-desc">Every rupee brings warmth, care, and dignity to abandoned seniors. Explore our key programs and make an impact now.</p>
          </div>

          <div className="home-section8-grid">
            <div className="home-section8-media">
              <div className="s8-collage">
                <img src={tabs[active].image} alt={tabs[active].title} className="s8-main" />
                <img src="/Images/story1.jpg" alt="Care" className="s8-small s8-top" />
                <img src="/Images/story2.jpg" alt="Smile" className="s8-small s8-bottom" />
                <div className="s8-floating-card">
                  <div className="s8-fc-line">
                    <FaUsers /><span>1200+ sheltered</span>
                  </div>
                  <div className="s8-fc-line">
                    <FaHandshake /><span>350+ active donors</span>
                  </div>
                  <div className="s8-fc-line">
                    <FaLeaf /><span>Wholesome nutrition</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="home-section8-content">
              <div className="s8-tabs">
                {tabs.map((t, i) => (
                  <button
                    key={t.key}
                    className={`s8-tab ${active === i ? 'active' : ''}`}
                    onClick={() => setActive(i)}
                  >
                    {t.title}
                    <i />
                  </button>
                ))}
              </div>

              <div className="s8-panel">
                <div className="s8-panel-body">
                  <ul className="s8-bullets">
                    {tabs[active].bullets.map((b, idx) => (
                      <li key={idx}>
                        <span className="s8-dot" />
                        <span>{b}</span>
                      </li>
                    ))}
                  </ul>
                  <div className="s8-badge-wrap">
                    <div className="s8-badge"><FaShieldAlt /> 80G eligible</div>
                    <div className="s8-badge"><FaShieldAlt /> Transparent reporting</div>
                  </div>
                </div>

                <div className="s8-pledge">
                  <div className="s8-amounts">
                    {quick.map((q) => (
                      <button
                        key={q}
                        className={`s8-amt ${amount === q ? 'active' : ''}`}
                        onClick={() => setAmount(q)}
                      >
                        <FaRupeeSign />{q.toLocaleString('en-IN')}
                      </button>
                    ))}
                  </div>
                  <div className="s8-input-row">
                    <div className="s8-input">
                      <FaRupeeSign className="rup" />
                      <input
                        type="number"
                        min="100"
                        value={amount}
                        onChange={(e) => setAmount(Number(e.target.value))}
                        aria-label="Enter amount"
                      />
                    </div>
                    <a href="/donation" className="s8-donate-btn">
                      <span className="s8-btn-icon">
                        <FaAngleDoubleRight className="small" />
                        <FaAngleDoubleRight className="big" />
                      </span>
                      Donate Now
                    </a>
                  </div>
                  <div className="s8-note">Even â‚¹500 sponsors a day of meals for one elder</div>
                </div>
              </div>

              <div className="s8-trust">
                <div className="s8-pill">Audit-ready records</div>
                <div className="s8-pill">Direct impact updates</div>
                <div className="s8-pill">Local community partners</div>
                <div className="s8-pill">Volunteer-friendly</div>
              </div>
            </div>
          </div>
        </div>
      </section>








      <Footer />
    </div>
  );
}

export default HomePage;
