import React, { useEffect, useRef, useState } from 'react';
import { FaHeart, FaAngleDoubleRight, FaChevronRight, FaHandsHelping, FaPhoneAlt, FaCheck, FaBookOpen, FaHeartbeat, FaTint, FaHome, FaBed, FaUtensils, FaUserNurse, FaShieldAlt, FaWheelchair, FaPlay, FaQuoteLeft, FaArrowRight, FaQuestionCircle, FaCalendarAlt, FaBullhorn, FaHandHoldingHeart, FaChevronDown } from 'react-icons/fa';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination, Navigation } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import Navbar from './Navbar';
import Footer from './Footer';
import './HomePage.css';


function AnimatedNumber({ end, duration }) {
  /* section 6 code */
  const [val, setVal] = useState(0);
  useEffect(() => {
    let start = 0;
    const startTime = performance.now();
    const step = (now) => {
      const p = Math.min((now - startTime) / duration, 1);
      const ease = 1 - Math.pow(1 - p, 3);
      const current = Math.floor(ease * end);
      if (current !== start) {
        start = current;
        setVal(current);
      }
      if (p < 1) requestAnimationFrame(step);
    };
    requestAnimationFrame(step);
  }, [end, duration]);
  return <>{val.toLocaleString()}</>;
}


function HomePage() {
  const [startCount, setStartCount] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    const io = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) setStartCount(true);
    }, { threshold: 0.3 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);

  const stats = [
    { label: 'Seniors Sheltered', end: 1200 },
    { label: 'Meals Served', end: 480000 },
    { label: 'Medical Checkups', end: 32000 },
    { label: 'Caregivers Trained', end: 850 }
  ];

  const features = [
    { icon: <FaHome />, title: 'Safe Homes' },
    { icon: <FaBed />, title: 'Comfortable Bedding' },
    { icon: <FaUtensils />, title: 'Nutritious Meals' },
    { icon: <FaUserNurse />, title: '24/7 Care' },
    { icon: <FaShieldAlt />, title: 'Security & Dignity' },
    { icon: <FaWheelchair />, title: 'Accessible Facilities' }
  ];
  /* end of section 6 code */


  /* section 7 code */
  const [visible, setVisible] = useState(false);
  const [activeTestimonial, setActiveTestimonial] = useState(0);
  const [openFaq, setOpenFaq] = useState(null);

  const milestones = [
    { icon: <FaCalendarAlt />, title: 'New Shelter Wing Opened', desc: '40 additional beds with accessible washrooms and warm lighting for comfort.', date: 'Aug 2025' },
    { icon: <FaBullhorn />, title: 'Winter Drive Announced', desc: 'Collecting blankets and thermals for elders in roadside communities.', date: 'Dec 2025' },
    { icon: <FaHandHoldingHeart />, title: 'Health Camp Series', desc: 'Monthly checkups with geriatric specialists and free medicine kits.', date: 'Janâ€“Mar 2026' }
  ];

  const testimonials = [
    { quote: 'I found peace and friends here. The evenings are full of stories and smiles.', name: 'Radha, 73', image: '/Images/story1.jpg' },
    { quote: 'They treat me with dignity. A warm bed and a warm heart make all the difference.', name: 'Imran, 79', image: '/Images/story2.jpg' },
    { quote: 'My medications are on time, and I never feel alone anymore.', name: 'Grace, 81', image: '/Images/story3.jpg' }
  ];

  const faqs = [
    { q: 'How can I sponsor a meal for seniors?', a: 'You can sponsor daily, weekly, or monthly meals. Visit the donation page, choose Meal Sponsorship, and select your frequency.' },
    { q: 'Do you provide medical support?', a: 'Yes. We run regular health camps, medicine support, and emergency care with partner hospitals.' },
    { q: 'Can I volunteer on weekends?', a: 'Absolutely. We welcome weekend volunteers for companionship, reading sessions, and activity support.' }
  ];

  useEffect(() => {
    const io = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) setVisible(true);
    }, { threshold: 0.25 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);

  useEffect(() => {
    const id = setInterval(() => {
      setActiveTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 4000);
    return () => clearInterval(id);
  }, [testimonials.length]);
  /* end of section 7 code */



  /* section 8 code */
  const goal = 750000;
  const raisedTarget = 582000;
  const percentTarget = Math.round((raisedTarget / goal) * 100);
  const [percent, setPercent] = useState(0);
  const [raised, setRaised] = useState(0);
  const [amount, setAmount] = useState(1000);
  const [email, setEmail] = useState('');

  useEffect(() => {
    const io = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) setVisible(true);
    }, { threshold: 0.35 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);

  useEffect(() => {
    if (!visible) return;
    const dur = 1400;
    const start = performance.now();
    const step = (now) => {
      const p = Math.min((now - start) / dur, 1);
      const ease = 1 - Math.pow(1 - p, 3);
      setPercent(Math.floor(ease * percentTarget));
      setRaised(Math.floor(ease * raisedTarget));
      if (p < 1) requestAnimationFrame(step);
    };
    requestAnimationFrame(step);
  }, [visible, percentTarget, raisedTarget]);

  const breakdown = [
    { label: 'Care & Housing', value: 42 },
    { label: 'Meals & Nutrition', value: 28 },
    { label: 'Medical Support', value: 20 },
    { label: 'Operations', value: 10 }
  ];

  const chips = [500, 1000, 2500, 5000, 10000];

  const handleDonate = (e) => {
    e.preventDefault();
    window.location.href = '/donation';
  };

  const handleSubscribe = (e) => {
    e.preventDefault();
  };
  
  /* end of section8 code */


  return (
    <div className="homepage-wrapper">
      <Navbar />

      <section className="home-section1-banner">
        <div className="home-section1-container">
          <div className="home-section1-row">
            <div className="home-section1-text">
              <div className="home-section1-header">
                <span className="home-section1-subtitle">
                  <FaHeart className="home-section1-icon" /> Change The World Together
                </span>
                <h1 className="home-section1-title">For The People & Cause You Care About</h1>
                <p className="home-section1-description">
                  It is a long established fact that a reader will be distracted lorem the readable
                  content of a page when looking at layout the point lorem established fact that It is a long established
                </p>
              </div>
              <div className="home-section1-buttons">
                <button className="home-section1-donate-btn">
                  <span className="home-section1-btn-icon">
                    <FaChevronRight className="small-icon" />
                    <FaChevronRight className="big-icon" />
                  </span>
                  Make Donation
                </button>
                <div className="home-section1-donor-card">
                  <div className="home-section1-images">
                    <img src="/Images/pic1.jpeg" alt="pic1" className="circle-pic" />
                    <img src="/Images/pic2.jpeg" alt="pic2" className="circle-pic" />
                    <img src="/Images/pic3.jpeg" alt="pic3" className="circle-pic" />
                    <div className="circle-pic overlay-count">2.5M</div>
                    <span className="home-section1-donor-label">Active <br /> donors</span>
                  </div>

                </div>
              </div>
              <img src="/Images/vector-img.png" alt="Vector" className="home-section1-vector" />
            </div>
            <div className="home-section1-image">
              <img src="/Images/banner-img.png" alt="Banner" className="home-section1-main-img" />
            </div>
          </div>
        </div>
      </section>


      <section className="home-section2-wrapper">
        <div className="home-section2-container">
          <div className="home-section2-row">
            <div className="home-section2-left">
              <div className="home-section2-img-wrapper">
                <img src="/Images/about-img.png" alt="About" className="home-section2-main-img" />
                <img src="/Images/about-img-vector-1.svg" alt="Vector" className="home-section2-vector-img" />
              </div>
            </div>
            <div className="home-section2-right">
              <div className="home-section2-heading">
                <span className="home-section2-subtitle">
                  <FaHeart className="icon" /> About US
                </span>
                <h2 className="home-section2-title">Helping Each Other can Make World Better</h2>
                <p className="home-section2-description">
                  When people unite with empathy and shared purpose, real change begins. Helping hands can bridge gaps,
                  spark hope, and create a world where no one feels left behind.
                </p>
              </div>

              <div className="home-section2-info-block">
                <div className="info-left">
                  <div className="info-title">
                    <div className="info-icon">
                      <FaHandsHelping className="icon" />
                    </div>
                    <h3>Start Helping Team</h3>
                  </div>
                  <ul className="info-list">
                    <li>
                      <span className="tick-circle">
                        <FaCheck className="tick-icon" />
                      </span>
                      There are many variations of solve
                    </li>
                  </ul>
                </div>
                <div className="info-right">
                  <img src="/Images/about-block-img.jpg" alt="Block" />
                </div>
              </div>

              <div className="home-section2-bottom">
                <button className="home-section2-btn">
                  <span className="btn-icon">
                    <FaAngleDoubleRight className="small" />
                    <FaAngleDoubleRight className="big" />
                  </span>
                  Explore More
                </button>
                <div className="call-block">
                  <div className="call-icon">
                    <FaPhoneAlt className="icon" />
                  </div>
                  <div className="call-text">
                    <span className="call-heading">Call Any Time</span>
                    <a href="tel:+911234567890">+91 1234567890</a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>








      <section className="home-section3-wrapper">
        <Swiper
          className="home-section3-slider"
          modules={[Autoplay, Pagination]}
          loop={true}
          autoplay={{ delay: 4000, disableOnInteraction: false }}
          pagination={{ clickable: true }}
        >


          <SwiperSlide>
            <div className="home-section3-slide" style={{ backgroundImage: 'url(/Images/banner-2-slide-1.jpg)' }}>
              <div className="home-section3-content">
                <span className="home-section3-subtitle"> <FaHeart className="icon" /> Change The World Together</span>
                <h1 className="home-section3-title">Empowering Hope Futures Lives Through Giving</h1>
                <p className="home-section3-description">
                  It is a long established fact that a reader will be distracted lorem the readable content of a page when looking at layout the point lorem established fact that It is a long established
                </p>
                <button className="home-section3-btn">
                  <span className="btn-icon">
                    <FaAngleDoubleRight className="small" />
                    <FaAngleDoubleRight className="big" />
                  </span>
                  Donate Now
                </button>
              </div>
            </div>
          </SwiperSlide>

          <SwiperSlide>
            <div className="home-section3-slide" style={{ backgroundImage: 'url(/Images/banner-2-slide-2.jpg)' }}>
              <div className="home-section3-content">
                <span className="home-section3-subtitle"><FaHeart className="icon" /> Change The World Together</span>
                <h1 className="home-section3-title">Empowering Hope Futures Lives Through Giving</h1>
                <p className="home-section3-description">
                  It is a long established fact that a reader will be distracted lorem the readable content of a page when looking at layout the point lorem established fact that It is a long established
                </p>
                <button className="home-section3-btn">
                  <span className="btn-icon">
                    <FaAngleDoubleRight className="small" />
                    <FaAngleDoubleRight className="big" />
                  </span>
                  Donate Now
                </button>
              </div>
            </div>
          </SwiperSlide>

          <SwiperSlide>
            <div className="home-section3-slide" style={{ backgroundImage: 'url(/Images/banner-2-slide-3.jpg)' }}>
              <div className="home-section3-content">
                <span className="home-section3-subtitle"><FaHeart className="icon" /> Change The World Together</span>
                <h1 className="home-section3-title">Empowering Hope Futures Lives Through Giving</h1>
                <p className="home-section3-description">
                  It is a long established fact that a reader will be distracted lorem the readable content of a page when looking at layout the point lorem established fact that It is a long established
                </p>
                <button className="home-section3-btn">
                  <span className="btn-icon">
                    <FaAngleDoubleRight className="small" />
                    <FaAngleDoubleRight className="big" />
                  </span>
                  Donate Now
                </button>
              </div>
            </div>
          </SwiperSlide>
        </Swiper>
      </section>










      <section className="home-section4-wrapper">
        <div className="home-section4-container">
          <div className="home-section4-row">
            <div className="home-section4-card">
              <div className="home-section4-icon">
                <FaBookOpen />
              </div>
              <h3 className="home-section4-title">Education Support</h3>
            </div>
            <div className="home-section4-card">
              <div className="home-section4-icon">
                <FaHeartbeat />
              </div>
              <h3 className="home-section4-title">Healthcare Initiatives</h3>
            </div>
            <div className="home-section4-card">
              <div className="home-section4-icon">
                <FaHandsHelping />
              </div>
              <h3 className="home-section4-title">Livelihood Programs</h3>
            </div>
            <div className="home-section4-card">
              <div className="home-section4-icon">
                <FaTint />
              </div>
              <h3 className="home-section4-title">Access to Water</h3>
            </div>
          </div>
        </div>
      </section>








      <section className="home-section5-wrapper">
        <div className="home-section5-container">
          <div className="home-section5-row">
            <div className="home-section5-left">
              <div className="home-section5-img-wrapper">
                <img src="/Images/about-2-img.jpg" alt="Experience" className="home-section5-img" />
                <div className="home-section5-experience">
                  <span className="number">15+</span>
                  <span className="txt">Years Of Experience</span>
                </div>
              </div>
            </div>
            <div className="home-section5-right">
              <span className="home-section5-subtitle"> <FaHeart className="icon" /> About US</span>
              <h2 className="home-section5-title">We Believe This Life's About Give For Poor People.</h2>
              <p className="home-section5-description">
                Dicta sunt explicabo. Nemo enim ipsam voluptatem quia voluptas sit The aspernaturaut odit aut fugit, sed quia
                consequuntur. Nonprofits around the world apply and join us to access more funding.
              </p>
              <div className="home-section5-blocks">
                <div className="home-section5-block">
                  <img src="/Images/mission.svg" alt="Mission" className="home-section5-icon" />
                  <div className="home-section5-block-text">
                    <h3>Our Mission</h3>
                    <p>The World Apply And Join us to Access Funding.</p>
                  </div>
                </div>
                <div className="home-section5-block">
                  <img src="/Images/vision.svg" alt="Vision" className="home-section5-icon" />
                  <div className="home-section5-block-text">
                    <h3>Our Vision</h3>
                    <p>The World Apply And Join us to Access Funding.</p>
                  </div>
                </div>
              </div>
              <a href="/" className="home-section5-btn">Explore More</a>
            </div>
          </div>
        </div>
      </section>




      <section className="home-section6-wrapper" ref={ref}>
      <div className="home-section6-container">
        <div className="home-section6-row">
          <div className="home-section6-media">
            <div className="home-section6-media-card">
              <img src="/Images/old1.jpg" alt="Elderly Care" className="home-section6-media-img" />
              <button className="home-section6-play">
                <FaPlay />
              </button>
              <div className="home-section6-badge">
                Hope & Care
              </div>
            </div>
          </div>

          <div className="home-section6-content">
            <span className="home-section6-subtitle">
              <FaHeart className="icon" /> Dignity For Every Elder
            </span>
            <h2 className="home-section6-title">A Warm Shelter, A Caring Hand, A Peaceful Life</h2>
            <p className="home-section6-desc">
              Your support gives abandoned seniors a safe place to sleep, daily meals, regular health checkups, and loving companionship. Together, we protect their dignity and bring peace to their evenings.
            </p>

            <div className="home-section6-features">
              {features.map((f, i) => (
                <div key={i} className="home-section6-feature">
                  <div className="home-section6-feature-icon">{f.icon}</div>
                  <div className="home-section6-feature-title">{f.title}</div>
                </div>
              ))}
            </div>

            <div className="home-section6-stats">
              {stats.map((s, i) => (
                <div key={i} className="home-section6-stat">
                  <div className="home-section6-stat-number">
                    {startCount ? (
                      <AnimatedNumber end={s.end} duration={1600 + i * 300} />
                    ) : 0}
                    {s.end >= 1000 && <span className="home-section6-plus">+</span>}
                  </div>
                  <div className="home-section6-stat-label">{s.label}</div>
                </div>
              ))}
            </div>

            <div className="home-section6-cta">
              <a href="/donation" className="home-section6-btn">
                <span className="home-section6-btn-icon">
                  <FaAngleDoubleRight className="small" />
                  <FaAngleDoubleRight className="big" />
                </span>
                Support A Senior Today
              </a>
              <a href="tel:+911234567890" className="home-section6-link">Need Help? Call +91 1234567890</a>
            </div>
          </div>
        </div>
      </div>
    </section>




    <section className={`home-section7-wrapper ${visible ? 'show' : ''}`} ref={ref}>
      <div className="home-section7-container">
        <div className="home-section7-head">
          <span className="home-section7-subtitle"><FaHeart className="icon" /> Stories, Updates, Answers</span>
          <h2 className="home-section7-title">Whatâ€™s New, Who We Help, How You Can Join</h2>
          <p className="home-section7-desc">Explore our latest milestones, hear from our elders, and get quick answers to common questions.</p>
        </div>

        <div className="home-section7-grid">
          <div className="home-section7-timeline">
            {milestones.map((m, i) => (
              <div key={i} className="s7-tl-item">
                <div className="s7-tl-dot">
                  <div className="s7-tl-dot-inner">{m.icon}</div>
                </div>
                <div className="s7-tl-card">
                  <div className="s7-tl-date">{m.date}</div>
                  <div className="s7-tl-title">{m.title}</div>
                  <div className="s7-tl-desc">{m.desc}</div>
                  <a href="/event" className="s7-tl-link">Learn More <FaArrowRight /></a>
                </div>
              </div>
            ))}
          </div>

          <div className="home-section7-spotlight">
            <div className="s7-spotlight-card">
              <div className="s7-spotlight-media">
                <img src={testimonials[activeTestimonial].image} alt={testimonials[activeTestimonial].name} />
                <div className="s7-spotlight-badge">Elder Voices</div>
              </div>
              <div className="s7-spotlight-quote">
                <FaQuoteLeft className="s7-quote-icon" />
                <p>{testimonials[activeTestimonial].quote}</p>
                <span className="s7-quote-name">{testimonials[activeTestimonial].name}</span>
              </div>
              <div className="s7-spotlight-dots">
                {testimonials.map((_, idx) => (
                  <button
                    key={idx}
                    className={`s7-dot ${idx === activeTestimonial ? 'active' : ''}`}
                    onClick={() => setActiveTestimonial(idx)}
                    aria-label={`Show testimonial ${idx + 1}`}
                  />
                ))}
              </div>
            </div>

            <div className="s7-info-pills">
              <a href="/donation" className="s7-pill">
                <span>â‚¹500</span>
                <em>1 Day Meals</em>
              </a>
              <a href="/donation" className="s7-pill">
                <span>â‚¹3,500</span>
                <em>Week of Care</em>
              </a>
              <a href="/donation" className="s7-pill">
                <span>â‚¹15,000</span>
                <em>Medical Support</em>
              </a>
              <a href="/donation" className="s7-pill">
                <span>â‚¹30,000</span>
                <em>Bed & Essentials</em>
              </a>
            </div>
          </div>

          <div className="home-section7-faq">
            <div className="s7-faq-list">
              {faqs.map((f, i) => (
                <div key={i} className={`s7-faq-item ${openFaq === i ? 'open' : ''}`}>
                  <button className="s7-faq-q" onClick={() => setOpenFaq(openFaq === i ? null : i)}>
                    <FaQuestionCircle className="q-icon" />
                    <span>{f.q}</span>
                    <FaChevronDown className="chev" />
                  </button>
                  <div className="s7-faq-a">
                    <p>{f.a}</p>
                  </div>
                </div>
              ))}
            </div>
            <div className="s7-cta">
              <a href="/contact" className="s7-cta-btn">
                Become A Volunteer
              </a>
              <a href="/donation" className="s7-cta-link">Prefer to donate? Start here</a>
            </div>
          </div>
        </div>
      </div>
    </section>


    <section className="home-section8-wrapper" ref={ref}>
      <div className="home-section8-container">
        <div className="home-section8-head">
          <span className="home-section8-subtitle"><FaHeart className="icon" /> Transparency & Support</span>
          <h2 className="home-section8-title">Fuel A Month Of Dignified Elder Care</h2>
          <p className="home-section8-desc">See our goal, how funds are used, and take a quick step to help.</p>
        </div>

        <div className="home-section8-grid">
          <div className="s8-goal">
            <div className="s8-ring">
              <div
                className="s8-ring-arc"
                style={{ background: `conic-gradient(var(--secondary) ${percent * 3.6}deg, rgba(255,255,255,0.12) 0deg)` }}
              />
              <div className="s8-ring-core">
                <div className="s8-percent">{percent}<span>%</span></div>
                <div className="s8-core-label">Monthly Goal</div>
              </div>
            </div>
            <div className="s8-figures">
              <div className="s8-fig">
                <div className="s8-fig-label">Raised</div>
                <div className="s8-fig-value">â‚¹{raised.toLocaleString()}</div>
              </div>
              <div className="s8-fig">
                <div className="s8-fig-label">Target</div>
                <div className="s8-fig-value">â‚¹{goal.toLocaleString()}</div>
              </div>
            </div>
            <a href="/donation" className="s8-cta">
              <span className="s8-cta-icon">
                <FaAngleDoubleRight className="small" />
                <FaAngleDoubleRight className="big" />
              </span>
              Contribute Now
            </a>
            <div className="s8-note">Every â‚¹500 covers one full day of meals for a senior.</div>
          </div>

          <div className="s8-breakdown">
            <div className="s8-break-title">Where Your Support Goes</div>
            <div className="s8-bars">
              {breakdown.map((b, i) => (
                <div key={i} className="s8-bar">
                  <div className="s8-bar-head">
                    <span>{b.label}</span>
                    <b>{b.value}%</b>
                  </div>
                  <div className="s8-bar-track">
                    <div className="s8-bar-fill" style={{ width: visible ? `${b.value}%` : '0%' }} />
                  </div>
                </div>
              ))}
            </div>
            <div className="s8-legend">
              <div className="s8-leg"><span style={{ background: 'var(--secondary)' }} /> Program Services</div>
              <div className="s8-leg"><span style={{ background: 'rgba(255,255,255,0.35)' }} /> Admin & Ops</div>
            </div>
          </div>

          <div className="s8-pledge">
            <div className="s8-pledge-title">Quick Pledge</div>
            <form className="s8-form" onSubmit={handleDonate}>
              <div className="s8-chips">
                {chips.map((c) => (
                  <button
                    key={c}
                    type="button"
                    className={`s8-chip ${amount === c ? 'active' : ''}`}
                    onClick={() => setAmount(c)}
                  >
                    â‚¹{c.toLocaleString()}
                  </button>
                ))}
              </div>
              <div className="s8-inputs">
                <input
                  type="number"
                  min="100"
                  placeholder="Custom amount (â‚¹)"
                  value={amount}
                  onChange={(e) => setAmount(Number(e.target.value))}
                  className="s8-input"
                />
                <input
                  type="email"
                  placeholder="Your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="s8-input"
                />
              </div>
              <button className="s8-donate-btn" type="submit">Donate â‚¹{Number(amount || 0).toLocaleString()}</button>
            </form>
            <form className="s8-subscribe" onSubmit={handleSubscribe}>
              <input
                type="email"
                placeholder="Get monthly updates"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="s8-sub-input"
              />
              <button className="s8-sub-btn" type="submit">Subscribe</button>
            </form>
          </div>
        </div>

        <div className="s8-partners">
          <div className="s8-marquee">
            <div className="s8-marquee-track">
              {['CareLink', 'SilverAge', 'HealTrust', 'MealMates', 'WarmHomes', 'SafeNights', 'KindCircle', 'GoodNeighbors'].map((p, i) => (
                <div key={i} className="s8-partner">{p}</div>
              ))}
              {['CareLink', 'SilverAge', 'HealTrust', 'MealMates', 'WarmHomes', 'SafeNights', 'KindCircle', 'GoodNeighbors'].map((p, i) => (
                <div key={`dup-${i}`} className="s8-partner">{p}</div>
              ))}
            </div>
          </div>
          <a href="/about" className="s8-partner-link">See more partners <FaArrowRight /></a>
        </div>
      </div>
    </section>







      <Footer />
    </div>
  );
}

export default HomePage;
