import React, { useEffect, useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaHeart, FaAngleDoubleRight, FaChevronRight, FaHandsHelping, FaPhoneAlt, FaCheck, FaBookOpen, FaHeartbeat, FaTint, FaHome, FaBed, FaUtensils, FaUserNurse, FaShieldAlt, FaWheelchair, FaPlay, FaQuoteLeft, FaArrowRight, FaQuestionCircle, FaCalendarAlt, FaBullhorn, FaHandHoldingHeart, FaChevronDown, FaRupeeSign, FaUsers, FaHandshake, FaLeaf, FaStethoscope, FaBalanceScale } from 'react-icons/fa';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination, Navigation } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import Navbar from './Navbar';
import Footer from './Footer';
import './HomePage.css';

function AnimatedNumber({ end, duration }) {
  const [val, setVal] = useState(0);
  useEffect(() => {
    let start = 0;
    const startTime = performance.now();
    const step = (now) => {
      const p = Math.min((now - startTime) / duration, 1);
      const ease = 1 - Math.pow(1 - p, 3);
      const current = Math.floor(ease * end);
      if (current !== start) {
        start = current;
        setVal(current);
      }
      if (p < 1) requestAnimationFrame(step);
    };
    requestAnimationFrame(step);
  }, [end, duration]);
  return <>{val.toLocaleString()}</>;
}

function HomePage() {
  const [startCount, setStartCount] = useState(false);
  const ref = useRef(null);
  const navigate = useNavigate();
  const onDonate = () => {
    navigate('/donation');
    window.scrollTo(0, 0);
  };

  useEffect(() => {
    const io = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) setStartCount(true);
    }, { threshold: 0.3 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);

  const stats = [
    { label: 'Seniors Sheltered', end: 1200 },
    { label: 'Meals Served', end: 480000 },
    { label: 'Medical Checkups', end: 32000 },
    { label: 'Caregivers Trained', end: 850 }
  ];

  const features = [
    { icon: <FaHome />, title: 'Safe Homes' },
    { icon: <FaBed />, title: 'Comfortable Bedding' },
    { icon: <FaUtensils />, title: 'Nutritious Meals' },
    { icon: <FaUserNurse />, title: '24/7 Care' },
    { icon: <FaShieldAlt />, title: 'Security & Dignity' },
    { icon: <FaWheelchair />, title: 'Accessible Facilities' }
  ];

  const [visible, setVisible] = useState(false);
  const [activeTestimonial, setActiveTestimonial] = useState(0);
  const [openFaq, setOpenFaq] = useState(null);

  const milestones = [
    { icon: <FaCalendarAlt />, title: 'New Shelter Wing Opened', desc: '40 additional beds with accessible washrooms and warm lighting for comfort.', date: 'Aug 2025' },
    { icon: <FaBullhorn />, title: 'Winter Drive Announced', desc: 'Collecting blankets and thermals for elders in roadside communities.', date: 'Dec 2025' },
    { icon: <FaHandHoldingHeart />, title: 'Health Camp Series', desc: 'Monthly checkups with geriatric specialists and free medicine kits.', date: 'Janâ€“Mar 2026' }
  ];

  const testimonials = [
    { quote: 'I found peace and friends here. The evenings are full of stories and smiles.', name: 'Radha, 73', image: '/Images/story1.jpg' },
    { quote: 'They treat me with dignity. A warm bed and a warm heart make all the difference.', name: 'Imran, 79', image: '/Images/story2.jpg' },
    { quote: 'My medications are on time, and I never feel alone anymore.', name: 'Grace, 81', image: '/Images/story3.jpg' }
  ];

  const faqs = [
    { q: 'How can I sponsor a meal for seniors?', a: 'You can sponsor daily, weekly, or monthly meals. Visit the donation page, choose Meal Sponsorship, and select your frequency.' },
    { q: 'Do you provide medical support?', a: 'Yes. We run regular health camps, medicine support, and emergency care with partner hospitals.' },
    { q: 'Can I volunteer on weekends?', a: 'Absolutely. We welcome weekend volunteers for companionship, reading sessions, and activity support.' }
  ];

  useEffect(() => {
    const io = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) setVisible(true);
    }, { threshold: 0.25 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);

  useEffect(() => {
    const id = setInterval(() => {
      setActiveTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 4000);
    return () => clearInterval(id);
  }, [testimonials.length]);

  const [active, setActive] = useState(0);
  const [amount, setAmount] = useState(1500);
  const [show, setShow] = useState(false);

  const tabs = [
    {
      key: 'shelter',
      title: 'Shelter & Comfort',
      image: '/Images/old1.jpg',
      bullets: ['Clean, safe rooms', 'Warm bedding & clothing', 'Companionship circles']
    },
    {
      key: 'health',
      title: 'Healthcare & Support',
      image: '/Images/old2.jpg',
      bullets: ['Regular checkups', 'Medicine support', 'Emergency care access']
    },
    {
      key: 'meals',
      title: 'Meals & Nutrition',
      image: '/Images/old3.jpg',
      bullets: ['Nutritious meals daily', 'Special diets for elders', 'Hydration & fruits']
    }
  ];

  const quick = [500, 1500, 3500, 10000];

  useEffect(() => {
    const io = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) setShow(true);
    }, { threshold: 0.25 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);

  useEffect(() => {
    const id = setInterval(() => {
      setActive((prev) => (prev + 1) % tabs.length);
    }, 5000);
    return () => clearInterval(id);
  }, [tabs.length]);

  useEffect(() => {
    const io = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) setShow(true);
    }, { threshold: 0.2 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);

  useEffect(() => {
    const io = new IntersectionObserver(([e]) => {
      if (e.isIntersecting) setShow(true);
    }, { threshold: 0.2 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);

  const FeaturePills = () => (
    <div className="home-section3-pills">
      <div className="pill"><FaBookOpen /> Education</div>
      <div className="pill"><FaStethoscope /> Health</div>
      <div className="pill"><FaHandHoldingHeart /> Women Empowerment</div>
      <div className="pill"><FaLeaf /> Environment</div>
      <div className="pill"><FaBalanceScale /> Legal Aid</div>
    </div>
  );

  const SlideContent = ({ subtitle, title, tagline, description }) => (
    <div className="home-section3-content">
      <span className="home-section3-subtitle"><FaHeart className="icon" /> {subtitle}</span>
      <h1 className="home-section3-title">{title}</h1>
      <h2 className="home-section3-tagline">{tagline}</h2>
      <p className="home-section3-description">{description}</p>
      <FeaturePills />
      <div className="home-section3-cta">
        <button className="home-section3-btn" onClick={onDonate}>
          <span className="btn-icon">
            <FaAngleDoubleRight className="small" />
            <FaAngleDoubleRight className="big" />
          </span>
          Donate Now
        </button>
        <Link to="/programs" className="home-section3-link">
          <span>Our Programs</span>
          <FaAngleDoubleRight className="link-arrow" />
        </Link>
      </div>
      <div className="home-section3-stats">
        <div className="stat">
          <span className="stat-num">10K+</span>
          <span className="stat-label">Lives Touched</span>
        </div>
        <div className="stat">
          <span className="stat-num">250+</span>
          <span className="stat-label">Village Camps</span>
        </div>
        <div className="stat">
          <span className="stat-num">5000+</span>
          <span className="stat-label">Students Guided</span>
        </div>
      </div>
    </div>
  );

  return (
    <div className="homepage-wrapper">
      <Navbar />

      <section className="home-section3-wrapper">
        <Swiper
          className="home-section3-slider"
          modules={[Autoplay, Pagination]}
          loop={true}
          autoplay={{ delay: 4500, disableOnInteraction: false }}
          pagination={{ clickable: true }}
        >
          <SwiperSlide>
            <div className="home-section3-slide" style={{ backgroundImage: 'url(/Images/banner-2-slide-1.jpg)' }}>
              <div className="home-section3-overlay">
                <div className="home-section3-shape shape-1" />
                <div className="home-section3-shape shape-2" />
                <div className="home-section3-shape shape-3" />
              </div>
              <SlideContent
                subtitle="Change The World Together"
                title="Mathrudevobhava Trust"
                tagline="Building dignity and opportunity in Andhra Pradesh"
                description="We enable families below the poverty line with education access, medical support, skill development, and community empowerment so every child can learn, every woman can earn, and every village can thrive."
              />
            </div>
          </SwiperSlide>

          <SwiperSlide>
            <div className="home-section3-slide" style={{ backgroundImage: 'url(/Images/banner-2-slide-2.jpg)' }}>
              <div className="home-section3-overlay">
                <div className="home-section3-shape shape-1" />
                <div className="home-section3-shape shape-2" />
                <div className="home-section3-shape shape-3" />
              </div>
              <SlideContent
                subtitle="Together For Better Futures"
                title="Mathrudevobhava Trust"
                tagline="Education, health, and sustainable livelihoods"
                description="From legal awareness to rural development, from environment camps to medical outreach and skill SIGs, we stand with communities to unlock opportunity with compassion and action."
              />
            </div>
          </SwiperSlide>

          <SwiperSlide>
            <div className="home-section3-slide" style={{ backgroundImage: 'url(/Images/banner-2-slide-3.jpg)' }}>
              <div className="home-section3-overlay">
                <div className="home-section3-shape shape-1" />
                <div className="home-section3-shape shape-2" />
                <div className="home-section3-shape shape-3" />
              </div>
              <SlideContent
                subtitle="Give Hope. Create Impact."
                title="Mathrudevobhava Trust"
                tagline="Your support powers change"
                description="Your donation helps us run education drives, health camps, women employment training, legal aid, and youth empowermentâ€”reaching those who need it most."
              />
            </div>
          </SwiperSlide>
        </Swiper>
      </section>

      <section className="home-section2-wrapper">
        <div className="home-section2-container">
